mapper.select:
SELECT bno, title, contents, writer, regdate, updatedate, image
FROM board
WHERE bno = #{bNo}

mapper.insert:
	  	
			SELECT NVL(MAX(bno), 100) + 1
			FROM board
		
  		INSERT INTO board
				  		(bno
				  		, title
				  		
				  		, contents
				  		
				  		, writer
				  		
				  		, regdate
				  		
				  		
				  		, image
				  		)
			  		VALUES
			  		(#{bNo}
			  		,#{title}
			  		
			  		,#{contents}
			  		
			  		,#{writer}
			  		
			  		,#{regDate}
			  		
			  		
			  		,#{image}
			  		)
